import React, { useState, useEffect } from 'react';
import MoviesList from './components/MoviesList';
import Form from './components/Form';
import './App.css';

import { initializeApp } from 'firebase/app';
import { getAuth, signInWithEmailAndPassword, onAuthStateChanged } from 'firebase/auth';

// Replace with your Firebase project configuration
const firebaseConfig = {
  apiKey: 'YOUR_API_KEY',
  authDomain: 'YOUR_AUTH_DOMAIN',
  projectId: 'YOUR_PROJECT_ID',
  storageBucket: 'YOUR_STORAGE_BUCKET',
  messagingSenderId: 'YOUR_MESSAGING_SENDER_ID',
  appId: 'YOUR_APP_ID',
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Get the authentication service
const auth = getAuth(app);

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);
  const [retryTimeout, setRetryTimeout] = useState(null);

  useEffect(() => {
    if (errorMessage) {
      const timeout = setTimeout(fetchMoviesHandler, 5000);
      setRetryTimeout(timeout);
    } else {
      if (retryTimeout) {
        clearTimeout(retryTimeout);
        setRetryTimeout(null);
      }
    }
  }, [errorMessage]);

  async function fetchMoviesHandler() {
    setIsLoading(true);
    setErrorMessage(null);

    try {
      const response = await fetch('https://react-swapi-9c359-default-rtdb.firebaseio.com/movies.json');
      if (!response.ok) {
        throw new Error("Something went wrong ... Retrying");
      }
      const data = await response.json();

      const transformMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });

      setMovies(transformMovies);
      setIsLoading(false);
    } catch (error) {
      setErrorMessage(error.message);
      setIsLoading(false);
    }
  }

  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        // User is authenticated
        // You can choose to do something here if needed
      }
    });
    fetchMoviesHandler();
  }, []);

  async function addMovieHandler(movie) {
    try {
      const user = auth.currentUser;
      if (!user) {
        throw new Error('User is not authenticated');
      }

      const response = await fetch('https://react-swapi-9c359-default-rtdb.firebaseio.com/movies.json', {
        method: 'POST',
        body: JSON.stringify(movie),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${await user.getIdToken()}`,
        }
      });

      if (!response.ok) {
        throw new Error('Failed to post data to Firebase');
      }

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  }

  function cancelRetryHandler() {
    if (retryTimeout) {
      clearTimeout(retryTimeout);
      setRetryTimeout(null);
      setErrorMessage(null);
    }
  }

  return (
    <React.Fragment>
      <section>
        <Form onAddMovie={addMovieHandler} />

        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {isLoading && <p>Loading...:-)</p>}
        {!isLoading && errorMessage && (
          <p>
            {errorMessage}
            <button onClick={cancelRetryHandler}>Cancel</button>
          </p>
        )}
        {!isLoading && !errorMessage && movies.length === 0 && <p>No Movies</p>}
        {!isLoading && !errorMessage && <MoviesList movies={movies} />}
      </section>
    </React.Fragment>
  );
}

export default App;





